try {
  // Remover markdown code blocks se existirem
  let jsonString = aiData;
  if (jsonString.includes('```json')) {
    jsonString = jsonString.split('```json')[1].split('```')[0];
  }

  // Limpar e extrair JSON
  jsonString = jsonString.trim();
  aiJson = JSON.parse(jsonString);
} catch (error) {
  console.log('Erro ao fazer parse do JSON da IA:', error);
  aiJson = {
    suggestedCategory: "Erro",
    subcategories: [],
    tags: [],
    summary: "Erro ao processar com IA",
    confidence: 0
  };
}

// Montar resposta final
return {
  documentId: webhookData.documentId,
  tenantId: webhookData.tenantId,
  category: webhookData.category,
  suggestedCategory: aiJson.suggestedCategory,
  subcategories: aiJson.subcategories,
  tags: aiJson.tags,
  summary: aiJson.summary,
  confidence: aiJson.confidence,
  processedAt: new Date().toISOString()
};

